using System.Security.Claims;using Fina.Api.Api;using Fina.Api.Commom.Api;using Fina.Core.Handlers;using Fina.Core.Requests.Categories;using Fina.Core.Responses;using Microsoft.AspNetCore.Mvc;namespace Fina.Api.EndPoints.Category;public class GetAllCategoryEndPoint:IEndpoint{    public static void Map(IEndpointRouteBuilder app)    =>app.MapPost("/{pageNumber}",HandleAsync)        .WithName("Categories: Get All")        .WithSummary("Recupera Todas as Categorias")        .WithDescription("Recupera Todas as Categorias")        .WithOrder(5)        .Produces<Response<Core.Models.Category?>>();    private static async Task<IResult> HandleAsync(        ClaimsPrincipal user,        ICategoryHandler handler,        [FromQuery] int pageNumber = ApiConfiguration.DefaultPageNumber,        [FromQuery]int pageSize= ApiConfiguration.DefaultPageSize)    {        var request = new GetAllCategoryRequest        {          UserId = ApiConfiguration.UserId,          PageNumber = pageNumber,          PageSize = pageSize,        };        var result =            await handler.GetAllAsync(request);        return result.IsSuccess            ? TypedResults.Ok(result)            : TypedResults.BadRequest(result);    }}